(function() {
	angular.module('bgtsforms',[])
	.run(function(){
		console.log('Forms Module Initialized');
	})
	.controller('SearchController',['$http',function($http){
		console.log('Search Controller');
		var searchCtrl	= this;
		searchCtrl.search	= function(url){
			$http.get(url, searchCtrl.q).then(function(r){
				console.log(r);
			});
		};
	}]);
})(); 