(function(){
	angular.module('mage',[])
	.run(function(){
		console.log('Mage APIs Initialized');
	})
	.service('MAGE',['$location','$http',function(location,http){
		var mage	= this; 
		mage.url	= location.absUrl();
		mage.api	= {
			search: 'api/rest/products?'
		};
		mage.search	= function(filter){
			http.get(mage.url + mage.api.search + filter).then(function(res){
				console.log('zxc');
				console.log(res);
				return res;
			});
		}
	}]);
})();